import React, {Component} from 'react';
import './App.css';
import Categories from './components/Categories/Categories'
import Feed from './components/Feed/Feed'

class App extends Component  {
  state = {
    images: [
      { id:1, url: "https://via.placeholder.com/320x300"},
      { id:2, url: "https://via.placeholder.com/320x300"},
      { id:3, url: "https://via.placeholder.com/320x300"},
      { id:4, url: "https://via.placeholder.com/320x300"},
      { id:5, url: "https://via.placeholder.com/320x300"},
      { id:6, url: "https://via.placeholder.com/320x300"},
      { id:7, url: "https://via.placeholder.com/320x300"},
      { id:8, url: "https://via.placeholder.com/320x300"},
      { id:9, url: "https://via.placeholder.com/320x300"},
      { id:10, url: "https://via.placeholder.com/320x300"}
    ],
  }

  async getCategories()
  {
      try{
          axios.defaults.headers.common['x-api-key'] = "8e805f31-3dfd-4cce-998c-e4337e5150ea" // Replace this with your API Key, as it's set to defaults it will be used from now onwards

          let response = await axios.get('https://api.thecatapi.com/v1/categories/' ) 
          this.categories = response.data;
          console.log("-- ("+this.categories.length +") Categories from TheCatAPI.com")

          // pick one to display initially
          this.selected_category = this.categories[2]
      }catch(err){
          console.log(err)
      }
  }

  render () {
    console.log(this.state.images[0].url)
    return (
      <div className="App">
        <Categories />
        <Feed images={this.state.images}/>
      </div>
    );
  }
}

export default App;
